svg.script:

const svgNS = "http://www.w3.org/2000/svg";
const width = 800;
const height = 600;
const fontSize = 16;
const columns = Math.floor(width / fontSize);
let drops = Array(columns).fill(0);

function matrix() {
  for(let i = 0; i < columns; i++) {
    let char = String.fromCharCode(33 + Math.floor(Math.random() * 94));
    let text = document.createElementNS(svgNS, "text");
    text.setAttribute("x", i * fontSize);
    text.setAttribute("y", drops[i] * fontSize);
    text.setAttribute("fill", "#0F0");
    text.setAttribute("font-family", "monospace");
    text.setAttribute("font-size", fontSize);
    text.textContent = char;
    document.documentElement.appendChild(text);
    drops[i]++;
    if(drops[i] * fontSize > height) drops[i] = 0;
  }

  // Remove old texts after a short delay
  setTimeout(() => {
    const texts = document.getElementsByTagName("text");
    while(texts.length > columns) texts[0].remove();
  }, 50);
}

setInterval(matrix, 50);
